+/* ckeditor_custom.css */
+
+/* Set body background color to white */
+body {
+    background-color: #ffffff;
+    font-family: "Calibri", sans-serif;  /* Using the Calibri font like Microsoft Word */
+    font-size: 14px;  /* Similar font size to Word's default */
+    line-height: 1.5;
+    margin: 10px;
 }
+
+/* Additional customization for fonts and text appearance */
+h1, h2, h3, h4, h5, h6 {
+    font-family: "Calibri", sans-serif;
+    font-weight: bold;
+}
+
+p {
+    font-family: "Calibri", sans-serif;
+    font-size: 14px;
+    color: #333333;  /* Dark gray text color like Word */
+    line-height: 1.5;
+}
+
+/* Ensure links are styled */
+a {
+    color: #1a0dab;
+    text-decoration: none;
+}
+
+/* Add table styling */
+table {
+    border-collapse: collapse;
+    width: 100%;
+}
+
+th, td {
+    border: 1px solid #d3d3d3;
+    padding: 8px;
+    text-align: left;
+}

