+    <!-- Map Initialization Script -->
+    <script>
+      function initMap() {
+          // Coordinates for the map center
+          const latitude = 40.713732;
+          const longitude = -74.0092704;

+          // Create a LatLng object
+          const myLatlng = { lat: latitude, lng: longitude };

+          // Initialize the map
+          const map = new google.maps.Map(document.getElementById("google-map"), {
+              zoom: 14,
+              center: myLatlng,
+              scrollwheel: false, // Disable zooming with scroll
+          });
+
+          // Add a marker to the map
+          new google.maps.Marker({
+              position: myLatlng,
+              map: map,
+              title: "Hello World!", // Optional: Tooltip for the marker
+          });
+      }
+
+      // Wait for the API to load and then initialize the map
+      window.onload = initMap;
+  </script>
