+<script>
+  // Function to toggle map visibility based on screen size
+  function toggleMaps() {
+    const screenWidth = window.innerWidth; // Get the screen width
+    const largeMap = document.getElementById('largeScreenMap');
+    const smallMap = document.getElementById('smallScreenMap');

+    if (screenWidth > 768) {
+      // Show large screen map and hide small screen map
+      largeMap.style.display = 'block';
+      smallMap.style.display = 'none';
+    } else {
+      // Show small screen map and hide large screen map
+      smallMap.style.display = 'block';
+      largeMap.style.display = 'none';
+    }
+  }
+
+  // Call the function on page load
+  toggleMaps();
+
+  // Attach event listener for screen resizing
+  window.addEventListener('resize', toggleMaps);
+</script>
